// src/App.js
import React, { useState } from 'react';
import axios from 'axios'; // Import Axios
import IngredientInput from './components/IngredientInput';  // Ваш компонент для вводу інгредієнтів
import RecipeList from './components/RecipeList';  // Ваш компонент для відображення рецептів

function App() {
    const [ingredients, setIngredients] = useState([]);
    const [recipes, setRecipes] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const addIngredient = (ingredient) => {
        if (ingredient.trim() !== '') {
            setIngredients([...ingredients, ingredient]);
        }
    };

    const getRecommendations = async () => {
        setLoading(true);
        setError('');
        try {
            const response = await axios.post("http://localhost:5000/api/recommendations", { ingredients });
            if (response.data.length === 0) {
                setError('No recommendations found for the selected ingredients.');
            } else {
                setRecipes(response.data);
            }
        } catch (error) {
            console.error("Error fetching recommendations:", error);
            setError('An error occurred while fetching recommendations.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <h1>Recipe Recommender</h1>
            
            {/* Ingredient input form */}
            <div>
                <input 
                    type="text" 
                    placeholder="Enter ingredient" 
                    onKeyDown={(e) => {
                        if (e.key === 'Enter') {
                            addIngredient(e.target.value);
                            e.target.value = ''; // clear input after adding
                        }
                    }} 
                />
                <button onClick={getRecommendations}>Get Recommendations</button>
            </div>

            {/* Display selected ingredients */}
            <div>
                <h3>Selected Ingredients:</h3>
                <ul>
                    {ingredients.map((ingredient, index) => (
                        <li key={index}>{ingredient}</li>
                    ))}
                </ul>
            </div>

            {/* Loading state */}
            {loading && <p>Loading...</p>}

            {/* Error state */}
            {error && <p style={{ color: 'red' }}>{error}</p>}

            {/* Display recipes */}
            {recipes.length > 0 && (
                <div>
                    <h2>Recommended Recipes</h2>
                    <ul>
                        {recipes.map((recipe) => (
                            <li key={recipe.recipe_id}>
                                <h3>{recipe.recipe_name}</h3>
                                <p><strong>Cuisine:</strong> {recipe.cuisine || 'N/A'}</p>
                                <p><strong>Dietary Info:</strong> {recipe.dietary_info || 'N/A'}</p>
                                <p><strong>Cooking Time:</strong> {recipe.cooking_time} minutes</p>
                                <p><strong>Ingredients:</strong> {recipe.ingredients.join(", ")}</p>
                                <p><strong>Instructions:</strong> {recipe.instructions}</p>
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
}

export default App;
