// src/App.js
import React, { useState } from 'react';
import axios from 'axios'; // Import Axios
import IngredientInput from './components/IngredientInput';
import RecipeList from './components/RecipeList';



function App() {
    const [ingredients, setIngredients] = useState([]);
    const [recipes, setRecipes] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const addIngredient = (ingredient) => {
        setIngredients([...ingredients, ingredient]);
    };

    const getRecommendations = async () => {
        setLoading(true);
        setError('');
        try {
            
            const response = await axios.post("http://localhost:5000/api/recommendations", { ingredients });
            if (response.data.length === 0) {
                
                setError('No recommendations found for the selected ingredients.');
            } else {
                setRecipes(response.data);
            }
        } catch (error) {
            console.error("Error fetching recommendations:", error);
            setError('An error occurred while fetching recommendations.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <h1>Recipe Recommender</h1>
            
            {/* Ingredient input form */}
            <div>
                <input 
                    type="text" 
                    placeholder="Enter ingredient" 
                    onKeyDown={(e) => {
                        if (e.key === 'Enter') {
                            addIngredient(e.target.value);
                            e.target.value = ''; // clear input after adding
                        }
                    }} 
                />
                <button onClick={getRecommendations}>Get Recommendations</button>
            </div>

            {/* Loading state */}
            {loading && <p>Loading...</p>}

            {/* Error state */}
            {error && <p style={{ color: 'red' }}>{error}</p>}

            {/* Display recipes */}
            {recipes.length > 0 && (
                <div>
                    <h2>Recommended Recipes</h2>
                    <ul>
                        {recipes.map((recipe) => (
                            <li key={recipe.recipe_id}>{recipe.recipe_name}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
}

export default App;
